import React,{useEffect, useState} from "react";
import ImageCard from "../ImageCard/ImageCard";
import { useDispatch, useSelector } from "react-redux";
import * as ReactBootstrap from 'react-bootstrap';
import {getAllImages, fetchAsyncImages} from "../../features/Images/imageSlice";
import './Tree.css';

const Tree = () => {
    const dispatch = useDispatch();
    useEffect(()=>{
        dispatch(fetchAsyncImages("festivals"));
    },[dispatch]);

    const [loading,setLoading]=useState(false);
    const images = useSelector(getAllImages);
    let renderImages= "";

    renderImages =
    images.hits.length>1? (
        images.hits.map((image, index) => (
        <ImageCard key={index} data={image} />
      ))
    ) : (
      <div>
        <h3>{images.Error}</h3>
      </div>
    );
    return (
        <div className="conatiner">
            <div className="image-container">{renderImages}</div>
            {<ReactBootstrap.Spinner animation="border" />}
        </div>
    );
};

export default Tree;
