import { createSlice, createAsyncThunk} from "@reduxjs/toolkit";
import imageApi from "../../common/apis/imageApi";
import axios from "axios";
import {APIKey} from "../../common/apis/imageApiKey";

export const fetchAsyncImages = createAsyncThunk(
    'Images/fetchAsyncImages', 
    async (term)=>{
        console.log(type(term));
    if(term)
    {
        const response= await axios.get('/{term}');
        return response.data;
    }
    else
    {
        const response= await axios.get('/api');
        return response.data;
    }

    //const response =  await imageApi.get(`?key=${APIKey}&q=${term}&image_type=photo`);
    //console.log(response.data);

});

const initialState={
    images:{
        "hits":[],
    },
};

const imageSlice= createSlice({
    name:"Images",
    initialState,
    reducers:{ 
        addImages:(state,{payload}) => {
            state.images=payload;
        },
    },
    extraReducers:{
        [fetchAsyncImages.pending]:() => {
            console.log("pending");
        },
        [fetchAsyncImages.fulfilled]:(state,{payload}) => {
            console.log("Success");
            return { ...state,images:payload};
        },
        [fetchAsyncImages.rejected]:() => {
            console.log("Rejected");
        },
    }
});

export const {addImages} = imageSlice.actions;
export const getAllImages=(state) => state.images.images;
export default imageSlice.reducer;
