import React,{useState} from "react";
import './Header.css';
import { useDispatch} from 'react-redux';
import {fetchAsyncImages} from "../features/Images/imageSlice"; 
// import {Link} from "react-router-dom";
// import ImageCard from './ImageCard/ImageCard';
// import { useNavigate } from 'react-router-dom';

const Header = () => {
    const [query, setQuery] = useState("");
    const [loading,setLoading]= useState(false);
    const dispatch=useDispatch();
    // const history = useNavigate();

    const submitHandler = (e) => {
        e.preventDefault();
        dispatch(fetchAsyncImages(query));
        setLoading(true);
        // history(`/${query}`);
        setQuery("");
      };

    return (
        <div>
            <div className="containerr">
                <form className="form" onSubmit={submitHandler} >
                    <input type="text" value={query} placeholder="search images" className="searchbar" onChange={(e)=> setQuery(e.target.value)}></input>
                    {<ReactBootstrap.Spinner animation="border" />}
                    <button class="buttonss" type="submit">Search</button>
                </form>
                <div className="button-images">
                        <button type="button" class="buttonss" onClick={(e) => { dispatch(fetchAsyncImages("Jaipur"));}}>Jaipur</button>
                        <button type="button" class="buttonss" onClick={(e) => { dispatch(fetchAsyncImages("Mumbai"));}}>Mumbai</button>
                        <button type="button" class="buttonss" onClick={(e) => { dispatch(fetchAsyncImages("Goa"));}}>Goa</button>
                    </div>
            </div>
            {/* <ImageCard query={query}/> */}
        </div>

    );
};

export default Header;
