import React,{useState} from "react";
import './Header.css';
import {Link, useNavigate} from "react-router-dom";

const Header = () => {
    const [query, setQuery] = useState("");
    const history = useNavigate();

    const submitHandler = (e) => {
        e.preventDefault();
        history(`/${query}`);
        setQuery(""); 
     }; 

    return (
        <div>
            <div className="containerr">
                <form className="form"  onSubmit={submitHandler}>
                    <input type="text" value={query} placeholder="search images" className="searchbar" onChange={(e)=> setQuery(e.target.value)}></input>
                    <button className="buttonss" type="submit" >Search</button>
                </form> 
                <div className="button-images">
                    {/* <Link to="/jaipur">
                    <button type="button" value="jaipur" className="buttonss">Jaipur</button>
                    </Link>
                    <Link to="/mumbai">
                    <button type="button" className="buttonss">Mumbai</button>
                    </Link>
                    <Link to="/goa">
                    <button type="button" className="buttonss">Goa</button>
                    </Link> */}
                    <button type="button" className="buttonss" onClick={(e) => { dispatch(fetchAsyncImages("Jaipur"));}}>Jaipur</button>
                    <button type="button" className="buttonss" onClick={(e) => { dispatch(fetchAsyncImages("Mumbai"));}}>Mumbai</button>
                    <button type="button" className="buttonss" onClick={(e) => { dispatch(fetchAsyncImages("Goa"));}}>Goa</button>
                </div>
            </div>
        </div>
    );
};

export default Header;
