import React,{useState, useEffect} from "react";
import './Header.css';
import { useDispatch} from 'react-redux';
import {fetchAsyncImages} from "../features/Images/imageSlice"; 
import Tree from "./Tree/Tree"; 


const Header = () => {
    const [query, setQuery] = useState("");
    const dispatch=useDispatch();

    const submitHandler = (e) => {
        e.preventDefault();
        dispatch(fetchAsyncImages(query));
        setQuery("");
      };

    const selectedButton = buttonSelected => {
        setQuery(buttonSelected);
        console.log(setQuery);
        dispatch(fetchAsyncImages(query));
        setQuery("");
   }

    return (
        <div>
            <div className="containerr">
                <form className="form" onSubmit={submitHandler} >
                    <input type="text" value={query} placeholder="search images" className="searchbar" onChange={(e)=> setQuery(e.target.value)}></input>
                    <button className="buttonss" type="submit">Search</button>
                </form>
                <div className="button-images">
                    <button type="button" className="buttonss" onClick={selectedButton} value="Jaipur">Jaipur</button>
                    <button type="button" className="buttonss" onClick={selectedButton} value="Mumbai">Mumbai</button>
                    <button type="button" className="buttonss" onClick={selectedButton} value="Goa">Goa</button>
                    {/* <button type="button" className="buttonss" onClick={(e) => { dispatch(fetchAsyncImages("Jaipur"));}}>Jaipur</button>
                    <button type="button" className="buttonss" onClick={(e) => { dispatch(fetchAsyncImages("Mumbai"));}}>Mumbai</button>
                    <button type="button" className="buttonss" onClick={(e) => { dispatch(fetchAsyncImages("Goa"));}}>Goa</button> */}
                </div>
            </div>
            <Tree data={query}/>
        </div>
    );
};

export default Header;
