import React,{useState} from "react";
import './Header.css';
import { useDispatch} from 'react-redux';
import {fetchAsyncImages} from "../features/Images/imageSlice"; 
import { useNavigate } from 'react-router-dom';

const Header = () => {
    const [query, setQuery] = useState("");
    const dispatch=useDispatch();
    const history = useNavigate();

    const submitHandler = (e) => {
        e.preventDefault();
        dispatch(fetchAsyncImages(query));
        setQuery("");
        // history(`/${query}`);
      };

      const handler = (e) => {
        e.preventDefault();
        dispatch(fetchAsyncImages("Jaipur"));
        setQuery("");
      };
    return (
        <div>
            <div className="container">
                <form className="form" onSubmit={submitHandler} >
                    <input type="text" value={query} placeholder="search images" className="searchbar" onChange={(e)=> setQuery(e.target.value)}></input>
                    <button type="submit">Search</button>
                </form>
                <div className="button-images">
                        <button type="button" onClick={handler}>Jaipur</button>
                        <button type="button">Mumbai</button>
                        <button type="button">Goa</button>
                    </div>
            </div>
        </div>
    );
};

export default Header;
