import { createSlice, createAsyncThunk} from "@reduxjs/toolkit";
import imageApi from "../../common/apis/imageApi";
import {APIKey} from "../../common/apis/imageApiKey";

const fetchAsyncImages = createAsyncThunk('Images/fetchAsyncImages', async ()=>{
        const response =  await imageApi.get(`?key=${APIKey}&q=${title}&image_type=photo`)
        .catch((err) =>{
            console.log("Err :",err);
        });
        dispatch(addImages(response.data));
});

const initialState={
    images:{
        "hits":[],
    },
};

const imageSlice= createSlice({
    name:"images",
    initialState,
    reducers:{ 
        addImages:(state,{payload}) => {
            state.images=payload;
        },
    },
});

export const {addImages} = imageSlice.actions;
export const getAllImages=(state) => state.images.images;
export default imageSlice.reducer;
