import React,{useState} from "react";
import './Header.css';
import Jaipur from "./Jaipur";
import { useHistory } from 'react-router-dom';
import Unsplash, {toJson} from "unsplash-js";

const unsplash = new Unsplash({
    accessKey: "uHl8LaELCvI_FQMj3YLiG6loDy0Az6w9cfuX4L4p-cE",
  });

const Header = () => {
    const [query, setQuery] = useState("");
    const [pics, setPics] = useState([]); 

    const searchPhotos = async (e) => {
        e.preventDefault();
        unsplash.search
        .photos(query)
        .then(toJson)
        .then((json) => {
            setPics(json.results);
        });
      };
    return (
        <div>
            <div className="container">
                <form className="form" onSubmit={searchPhotos}>
                    <input type="text" name="query" placeholder="search images" value={query} onChange={(e) => setQuery(e.target.value)} className="searchbar"></input>
                    <button type="submit">Search</button>
                </form>
                <div className="button-images">
                        <button type="button">Jaipur</button>
                        <button type="button">Mumbai</button>
                        <button type="button">Goa</button>
                    </div>
                <div className="card-list">
                    {pics.map((pic) =>
                    <div className="card" key={pic.id}>
                        <img
                        className="card--image"
                        alt={pic.alt_description}
                        src={pic.urls.full}
                        width="50%"
                        height="50%"
                        ></img>
                    </div>)}
                </div>

            </div>
            <Jaipur/>
        </div>
    );
};

export default Header;
