import React,{useState} from "react";
import './Header.css';
import Jaipur from "./Jaipur";
import { useDispatch} from 'react-redux';
import {fetchAsyncImages} from "../features/Images/imageSlice"; 
import { useHistory } from 'react-router-dom';

const Header = () => {
    const [query, setQuery] = useState("");
    const dispatch=useDispatch();
    const history = useHistory();

    const submitHandler = (e) => {
        e.preventDefault();
        dispatch(fetchAsyncImages(query));
        history.push(`/${query}`);
      };
    return (
        <div>
            <div className="container">
                <form className="form" onSubmit={submitHandler} >
                    <input type="text" value={query} placeholder="search images" className="searchbar" onChange={(e)=> setQuery(e.target.value)}></input>
                    <button type="submit">Search</button>
                </form>
                <div className="button-images">
                        <button type="button">Jaipur</button>
                        <button type="button">Mumbai</button>
                        <button type="button">Goa</button>
                    </div>

            </div>
        </div>
    );
};

export default Header;
